using System;
using System.Collections.Generic;
using System.Data;
using System.Linq;
using System.Web;
using System.Web.UI;
using System.Web.UI.WebControls;

namespace Nhom5_QuanLySuaChuaBaoHanh.Views
{
    public partial class TrangChu : System.Web.UI.Page
    {
        protected void Page_Load(object sender, EventArgs e)
        {
            Repeater1.DataSource = GetDataFromDatabase();
            Repeater1.DataBind();
        }

        protected void Page_PreInit(object sender, EventArgs e)
        {
            string masterPage = Request.QueryString["mp"];

            if (!string.IsNullOrEmpty(masterPage))
            {
                 this.MasterPageFile = masterPage;
            }
        }

        private DataTable GetDataFromDatabase()
        {
            // Tạo DataTable mới với các cột được yêu cầu
            DataTable dt = new DataTable();
            dt.Columns.Add("Mã DVSC", typeof(string));
            dt.Columns.Add("Tên DVSC", typeof(string));
            dt.Columns.Add("Hình ảnh", typeof(string));


            // Thêm các hàng vào DataTable
            dt.Rows.Add("1", "Sửa chữa tủ lạnh", "data:image/jpeg;base64,");
            dt.Rows.Add("2", "Sửa chữa điều hòa", "data:image/jpeg;base64,");
            dt.Rows.Add("3", "Sửa chữa bình nóng lạnh", "https://suadienlanhhanoi247.com/wp-content/uploads/2021/10/sua-binh-nong-lanh-huyen-binh-chanh.jpg");
            dt.Rows.Add("4", "Sửa chữa máy giặt", "https://dienlanhcholon.com/wp-content/uploads/2019/06/sua-may-giat-hcm.jpg");
            dt.Rows.Add("5", "Sửa chữa lò vi sóng", "https://baohanh-sharp.com/hinh-anh/tin-tuc/tin-tuc-1620115300.jpg");
            dt.Rows.Add("6", "Sửa chữa máy lọc không khí", "https://inhat.vn/hcm/wp-content/uploads/2022/06/sua-may-loc-khong-khi-tphcm-3-min.jpg");

            return dt;
        }
    }
}